import React, { useState } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Logo from './Logo';
import riskImg from '../assets/images/risk.png';
import epImg from '../assets/images/ep.png';
import secImg from '../assets/images/security.png';

function AboutUs() {
  const [expandedContent, setExpandedContent] = useState({
    pioneering: false,
    redefining: false,
    elevating: false,
  });

  const toggleExpandedContent = (section) => {
    setExpandedContent({
      ...expandedContent,
      [section]: !expandedContent[section],
    });
  };

  return (
    <div className="about-us-container">
      <Container className="my-5">
        <h1 className="text-center mb-4">About Us</h1>
        <Logo />

        <Row className="align-items-center mb-5">
          <Col md={6}>
            <div className="p-4">
              <h4>Pioneering the Future of Security</h4>
              <p>
                Integrity Protection and Consulting Corp was born out of a vision to revolutionize the security landscape in Canada. With our partners bringing a wealth of experience from diverse business backgrounds, we embarked on a mission to elevate the standards of the security industry.
              </p>
              {expandedContent.pioneering ? (
                <div>
                  <p>
                    Additional content for Pioneering the Future of Security can be added here.
                  </p>
                  <Button
                    variant="primary"
                    className="d-block d-md-inline-block"
                    onClick={() => toggleExpandedContent('pioneering')}
                  >
                    Show Less
                  </Button>
                </div>
              ) : (
                <Button
                  variant="primary"
                  className="d-block d-md-inline-block"
                  onClick={() => toggleExpandedContent('pioneering')}
                >
                  Learn More
                </Button>
              )}
            </div>
          </Col>
          {/* Rest of the content sections with similar structure */}
        </Row>
        {/* Rest of the content sections with similar structure */}
      </Container>
    </div>
  );
}

export default AboutUs;
