import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Home from "./Home";
import About from "./About";
import ServicePage from "./ServicePage";

function Header() {
  const [fetchedComponent, setFetchedComponent] = useState(null);

  const handleComponentChange = (componentName) => {
    fetch(`/get_component/${componentName}`)
      .then((response) => response.json())
      .then((data) => {
        const { component } = data;
        if (component) {
          console.log(`Rendering ${component}`);
          setFetchedComponent(component); // Save the fetched component data
        }
      })
      .catch((error) => {
        console.error('Error fetching component data:', error);
      });
  };

  useEffect(() => {
    // This useEffect will trigger a re-render when fetchedComponent changes
    // Now, render the fetched component
    const renderComponent = () => {
      switch (fetchedComponent) {
        case 'Home':
          return <Home />;
        case 'About':
          return <About />;
        case 'Services':
          return <ServicePage />;
        default:
          return null;
      }
    };

    // Render the fetched component
    renderComponent();
  }, [fetchedComponent]);

  return (
    <header className="bg-dark">
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container">
          <Link to="/" className="navbar-brand">Integrity Protection & Consulting Corp.</Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('Home')}>
                  Home
                </button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('About')}>
                  About
                </button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('Services')}>
                  Services
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      {/* Render the fetched component */}
      <div>
        {fetchedComponent === 'Home' && <Home />}
        {fetchedComponent === 'About' && <About />}
        {fetchedComponent === 'Services' && <ServicePage />}
      </div>
    </header>
  );
}

export default Header;
