import React, { useState, useEffect } from 'react';
import Carousel from 'react-bootstrap/Carousel';

const SponsorsCarousel = () => {
  const sponsorNames = ['Sponsor A', 'Sponsor B', 'Sponsor C', 'Certification X', 'Certification Y'];
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex === sponsorNames.length - 1 ? 0 : prevIndex + 1));
    }, 3000); // Change slide every 3 seconds (adjust the timing as needed)
    return () => clearInterval(interval);
  }, [sponsorNames.length]);

  return (
    <Carousel activeIndex={index} onSelect={() => {}}>
      {sponsorNames.map((name, idx) => (
        <Carousel.Item key={idx}>
          <div className="d-flex justify-content-center align-items-center carousel-item">
            <h3>{name}</h3>
          </div>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default SponsorsCarousel;
