import React, { useState } from 'react';
import axios from 'axios';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      
      // Send form data to Flask API
      await axios.post('http://127.0.0.1:5000/submit-form', formData);

      alert("Message Sending Thank You For Your Interest!")
      // Send email to company
      await axios.post('http://127.0.0.1:5000/send-email', {
        to: 'company@example.com',
        subject: 'Contact Form Submission',
        body: formData.message // You might need to structure the email body accordingly
      });

      // Reset the form after successful submission
      setFormData({
        name: '',
        email: '',
        phone: '',
        message: '',
        companyName: '',
        location: ''
      });
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <section className="contact-section py-5">
      <div className="container">
        <div className="row justify-content-center">
          {/* Contact Form */}
          <div className="col-lg-8">
            <h2 className="text-center mb-4">Contact a Consultant Today</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <input
                  type="email"
                  className="form-control"
                  placeholder="Email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Company Name"
                  name="companyName"
                  value={formData.companyName}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Location"
                  name="location"
                  value={formData.location}
                  onChange={handleChange}
                />
              </div>
              <div className="mb-3">
                <textarea
                  className="form-control"
                  placeholder="Message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  rows="5"
                ></textarea>
              </div>
              <div className="d-grid">
                <button type="submit" className="btn btn-primary">Submit</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
