import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Home from "./Home";
import About from "./About";
import ServicePage from "./ServicePage";

function Header() {
  const [selectedComponent, setSelectedComponent] = useState('Home'); // Default component

  const handleComponentChange = (componentName) => {
    setSelectedComponent(componentName);
  };

  // Define the component based on the selectedComponent state
  const renderSelectedComponent = () => {
    switch (selectedComponent) {
      case 'Home':
        return <Home />;
      case 'About':
        return <About />;
      case 'Services':
        return <ServicePage />;
      default:
        return null;
    }
  };

  return (
    <header className="bg-dark">
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container">
          <Link to="/" className="navbar-brand">Integrity Protection & Consulting Corp.</Link>
          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarNav"
            aria-controls="navbarNav"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('Home')}>
                  Home
                </button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('About')}>
                  About
                </button>
              </li>
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('Services')}>
                  Services
                </button>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      {/* Render the selected component */}
      <div>
        {renderSelectedComponent()}
      </div>
    </header>
  );
}

export default Header;
