import React, { useState, useEffect } from 'react';
import Home from './Home';
import About from './About';
import ServicePage from './ServicePage';

function Header() {
  const [fetchedComponent, setFetchedComponent] = useState(null);

  useEffect(() => {
    // Assuming you have a default component to render initially (e.g., Home)
    const defaultComponent = 'HomeComponent'; // Update this to match your default component name
    fetchComponent(defaultComponent);
  }, []);

  const fetchComponent = (componentName) => {
    fetch(`/get_component/${componentName}`)
      .then((response) => response.json())
      .then((data) => {
        const { component } = data;
        if (component) {
          console.log(`Rendering ${component}`);
          setFetchedComponent(component); // Save the fetched component data
        }
      })
      .catch((error) => {
        console.error('Error fetching component data:', error);
      });
  };

  let ComponentToRender;

  switch (fetchedComponent) {
    case 'HomeComponent':
      ComponentToRender = Home;
      break;
    case 'AboutComponent':
      ComponentToRender = About;
      break;
    case 'ServicesComponent':
      ComponentToRender = ServicePage;
      break;
    default:
      ComponentToRender = null;
  }

  return (
    <header className="bg-dark">
    <img src="integrity\src\assets\images\logo.png" />
      <nav className="navbar navbar-expand-lg navbar-dark">
        <ul className="navbar-nav">
          <li className="nav-item">
            <a href="#" className="nav-link" onClick={() => fetchComponent('HomeComponent')}>
              Home
            </a>
          </li>
          <li className="nav-item">
            <a href="#" className="nav-link" onClick={() => fetchComponent('AboutComponent')}>
              About
            </a>
          </li>
          <li className="nav-item">
            <a href="#" className="nav-link" onClick={() => fetchComponent('ServicesComponent')}>
              Services
            </a>
          </li>
        </ul>
        <div>
          {/* Render the fetched component directly */}
          {ComponentToRender && <ComponentToRender />}
        </div>
      </nav>
    </header>
  );
}

export default Header;
