import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Home from './Home';
import About from './About';
import ServicePage from './ServicePage';

function Header() {
  const [fetchedComponent, setFetchedComponent] = useState(null);

  const handleComponentChange = (componentName) => {
    fetch(`/get_component/${componentName}`)
      .then((response) => response.json())
      .then((data) => {
        const { component } = data;
        if (component) {
          console.log(`Rendering ${component}`);
          setFetchedComponent(component); // Save the fetched component data
        }
      })
      .catch((error) => {
        console.error('Error fetching component data:', error);
      });
  };

  let ComponentToRender;

  switch (fetchedComponent) {
    case 'HomeComponent':
      ComponentToRender = Home;
      break;
    case 'AboutComponent':
      ComponentToRender = About;
      break;
    case 'ServicesComponent':
      ComponentToRender = ServicePage;
      break;
    default:
      ComponentToRender = null;
  }

  return (
    <header className="bg-dark">
      <nav className="navbar navbar-expand-lg navbar-dark">
        {/* ... your navigation elements ... */}
        <div>
          {/* Render the fetched component directly */}
          {ComponentToRender && <ComponentToRender />}
        </div>
      </nav>
    </header>
  );
}

export default Header;
