import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Home from "./Home";
import About from "./About";
import ServicePage from "./ServicePage";

function Header() {
  const [fetchedComponent, setFetchedComponent] = useState(null);

  const handleComponentChange = (componentName) => {
    fetch(`/get_component/${componentName}`)
      .then((response) => response.json())
      .then((data) => {
        const { component } = data;
        if (component) {
          console.log(`Rendering ${component}`);
          setFetchedComponent(component); // Save the fetched component data
        }
      })
      .catch((error) => {
        console.error('Error fetching component data:', error);
      });
  };

  useEffect(() => {
    // Trigger a re-render when fetchedComponent changes
    renderComponent();
  }, [fetchedComponent]);

  const renderComponent = () => {
    switch (fetchedComponent) {
      case 'Home':
        return <Home />;
      case 'About':
        return <About />;
      case 'Services':
        return <ServicePage />;
      default:
        return null;
    }
  };

  return (
    <header className="bg-dark">
      <nav className="navbar navbar-expand-lg navbar-dark">
        <div className="container">
          {/* Your navigation links */}
          <Link to="/" className="navbar-brand">Your Brand</Link>
          <button
            className="navbar-toggler"
            // ... (your toggle button)
          >
            {/* ... (your toggler icon) */}
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item">
                <button className="nav-link" onClick={() => handleComponentChange('Home')}>
                  Home
                </button>
              </li>
              {/* Repeat the pattern for other links */}
            </ul>
          </div>
        </div>
      </nav>
      {/* Render the fetched component */}
      <div>
        {renderComponent()}
      </div>
    </header>
  );
}

export default Header;
