import React from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import Logo from './Logo';
import riskImg from '../assets/images/risk.png';
import epImg from '../assets/images/ep.png';
import secImg from '../assets/images/security.png';

function AboutUs() {
  // Function to handle scrolling to different sections
  const handleScroll = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="about-us-container">
      <Container className="my-5">
        <h1 className="text-center mb-4">About Us</h1>
        <Logo />

        {/* First Section */}
        <Row className="align-items-center mb-5">
          <Col md={6}>
            <div className="p-4">
              <h4>Pioneering the Future of Security</h4>
              <p>Integrity Protection and Consulting Corp was born out of a vision to revolutionize the security landscape in Canada.</p>
              {/* Use a Link to navigate to a different route */}
              <Link to="/learn-more1">
                <Button variant="primary" className="d-block d-md-inline-block">
                  Learn More
                </Button>
              </Link>
            </div>
          </Col>
          <Col md={6}>
            <img src={riskImg} alt="Visual content" className="img-fluid" />
          </Col>
        </Row>

        {/* Second Section */}
        <Row className="align-items-center mb-5">
          <Col md={6}>
            <img src={epImg} alt="Visual content" className="img-fluid" />
          </Col>
          <Col md={6}>
            <div className="p-4">
              <h4>Redefining Security in Canada</h4>
              <p>Integrity Protection and Consulting Corp is a trailblazer in the Canadian security landscape.</p>
              {/* Use handleScroll to scroll to a specific section */}
              <Button variant="primary" className="d-block d-md-inline-block" onClick={() => handleScroll('section2')}>
                Learn More
              </Button>
            </div>
          </Col>
        </Row>

        {/* Third Section */}
        <Row id="section2" className="align-items-center mb-5">
          <Col md={6}>
            <div className="p-4">
              <h4>Elevating Security with Integrity</h4>
              <p>At Integrity Protection and Consulting Corp, we believe that true security goes beyond just safeguarding assets.</p>
              {/* Use handleScroll to scroll to a specific section */}
              <Button variant="primary" className="d-block d-md-inline-block" onClick={() => handleScroll('section3')}>
                Learn More
              </Button>
            </div>
          </Col>
          <Col md={6}>
            <img src={secImg} alt="Visual content" className="img-fluid" />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default AboutUs;
