import React from 'react';
import certification1Image from '../assets/images/certification1.png';
import certification2Image from '../assets/images/certification2.png';

function Certifications() {
  const certifications = [
    {
      id: 1,
      title: 'Certification Title 1',
      organization: 'Certification Organization 1',
      date: 'Date 1',
      image: certification1Image,
    },
    {
      id: 2,
      title: 'Certification Title 2',
      organization: 'Certification Organization 2',
      date: 'Date 2',
      image: certification2Image,
    },
  ];

  return (
    <section className="certifications-section">
      <div className="container">
        <div className="row">
          {certifications.map(certification => (
            <div key={certification.id} className="col-md-6 mb-4">
              <div className="card">
                <img src={certification.image} className="card-img-top" alt={certification.title} />
                <div className="card-body">
                  <h5 className="card-title">{certification.title}</h5>
                  <p className="card-text">Organization: {certification.organization}</p>
                  <p className="card-text">Date: {certification.date}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Certifications;
