import React, { useState } from 'react';
import axios from 'axios';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send email to company
      await axios.post('YOUR_COMPANY_EMAIL_ENDPOINT', {
        to: 'company@example.com',
        subject: 'Contact Form Submission',
        body: formData.message // You might need to structure the email body accordingly
      });

      // Send form data to Flask API
      await axios.post('http://127.0.0.1:5000/submit-form', formData);

      // Reset the form after successful submission
      setFormData({
        name: '',
        email: '',
        phone: '',
        message: '',
        companyName: '',
        location: ''
      });
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <section className="contact-section py-5">
      <div className="container">
        <div className="row justify-content-center">
          {/* Contact Information */}
          <div className="col-lg-4">
            <h2>Contact Information</h2>
            {/* Include your company's contact details */}
          </div>

          {/* Contact Form */}
          <div className="col-lg-8">
            <h2>Contact a Consultant Today</h2>
            <form onSubmit={handleSubmit}>
              <label for="Name">Name:</label>
              <input name="Name" type="text"></input>
              <label for="Email">Email:</label>
              <input name="Email" type="email"></input>
              <label for="Phone">Phone Number:</label>
              <input name="Phone" type="text"></input>
              <label for="Message">Message:</label>
              <textarea name="Message" maxLength={300}></textarea>
              <label for="Business">Business Name:</label>
              <input name="Business" type="text"></input>
              <label for="Location">Location:</label>
              <input name="Location" type="text"></input>
              <button type="submit">Submit</button>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
