import React, { useState } from 'react';
import { Form, Row, Col, Button } from 'react-bootstrap';
import axios from 'axios';
import '../styles/contactForm.css'; // Import your custom styles here

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/submit-form', formData);
      alert('Form submitted successfully!');
      // Additional logic after successful form submission if needed
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('There was an error submitting the form to the application');
    }
  };

  return (
    <Row className="contact-form-container">
      {/* Form Column */}
      <Col md={6} className="form-column">
        <Form onSubmit={handleSubmit} className="my-5">
          <h4>Contact A Consultant Today!</h4>
          <Form.Group controlId="formName">
            <Form.Control
              type="text"
              placeholder="Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              className="form-input"
            />
          </Form.Group>
          {/* Add other Form.Group components similarly */}
          <Button variant="primary" type="submit" className="submit-button">Submit</Button>
        </Form>
      </Col>

      {/* Contact Information Column */}
      <Col md={6} className="info-column">
        <div className="contact-info">
          <h4>Contact A Consultant Today!</h4>
          <p>Edmonton, AB, Canada</p>
          <p>business@icpp.ca</p>
        </div>
      </Col>
    </Row>
  );
};

export default ContactForm;
