import React, { useState } from 'react';
import axios from 'axios';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send email to company
      await axios.post('YOUR_COMPANY_EMAIL_ENDPOINT', {
        to: 'company@example.com',
        subject: 'Contact Form Submission',
        body: formData.message // You might need to structure the email body accordingly
      });

      // Send form data to Flask API
      await axios.post('http://127.0.0.1:5000/submit-form', formData);

      // Reset the form after successful submission
      setFormData({
        name: '',
        email: '',
        phone: '',
        message: '',
        companyName: '',
        location: ''
      });
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <section className="contact-section py-5">
      <div className="container">
        <div className="row justify-content-center">
          {/* Contact Information */}
          <div className="col-lg-4">
            <h2>Contact Information</h2>
            {/* Include your company's contact details */}
          </div>

          {/* Contact Form */}
          <div className="col-lg-8">
            <h2>Contact a Consultant Today</h2>
            <form onSubmit={handleSubmit}>
              {/* Form fields for Name, Email, Phone, Message, Business Name, Location */}
              {/* Use input fields for each piece of information */}
              {/* Add event handlers for input changes */}
              {/* Add Submit button */}
            </form>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
