import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Home from "./Home";
import About from "./About";
import ServicePage from "./ServicePage";

function Header() {
  const [fetchedComponent, setFetchedComponent] = useState(null);

  const handleComponentChange = (componentName) => {
    fetch(`/get_component/${componentName}`)
      .then((response) => response.json())
      .then((data) => {
        const { component } = data;
        if (component) {
          console.log(`Rendering ${component}`);
          setFetchedComponent(component); // Save the fetched component data
        }
      })
      .catch((error) => {
        console.error('Error fetching component data:', error);
      });
  };

  useEffect(() => {
    // This useEffect will trigger a re-render when fetchedComponent changes
    // Now, render the fetched component
    const renderComponent = () => {
      switch (fetchedComponent) {
        case 'Home':
          return <Home />;
        case 'About':
          return <About />;
        case 'Services':
          return <ServicePage />;
        default:
          return null;
      }
    };

    // Render the fetched component
    renderComponent();
  }, [fetchedComponent]);

  return (
    <header className="bg-dark">
      <nav className="navbar navbar-expand-lg navbar-dark">
        {/* ... your navigation elements ... */}
        {/* Place where the component will be rendered */}
        <div>
          {fetchedComponent === 'Home' && <Home />}
          {fetchedComponent === 'About' && <About />}
          {fetchedComponent === 'Services' && <ServicePage />}
        </div>
      </nav>
    </header>
  );
}

export default Header;
