import React, { useState } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';

const LearnOne = () => {
  const [expandedContent, setExpandedContent] = useState(false);

  const toggleExpandedContent = () => {
    setExpandedContent(!expandedContent);
  };

  return (
    <Container className="my-5">
      <Row className="align-items-center mb-5">
        <Col md={6}>
          <div className="p-4">
            <h4>Pioneering the Future of Security</h4>
            <p>
              Integrity Protection and Consulting Corp was born out of a vision to revolutionize the security landscape in Canada. With our partners bringing a wealth of experience from diverse business backgrounds, we embarked on a mission to elevate the standards of the security industry.
            </p>
            {expandedContent ? (
              <div>
                <p>
                  Our journey involves implementing innovative technologies and strategic partnerships to redefine the security landscape, ensuring a safer environment for all.
                </p>
                {/* Add more detailed content here */}
                <Button variant="primary" onClick={toggleExpandedContent}>Show Less</Button>
              </div>
            ) : (
              <Button variant="primary" onClick={toggleExpandedContent}>Learn More</Button>
            )}
          </div>
        </Col>
        {/* Add image or visual content in the other column */}
      </Row>
      {/* Additional rows or sections can be added here for more content */}
    </Container>
  );
};

export default LearnOne;
