import React, { useState } from 'react';
import axios from 'axios';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/submit-form', formData);
      alert('Form submitted successfully!');
      // Additional logic after successful form submission if needed
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('There was an error submitting the form to the application');
    }
  };

  return (
    <div className="contact-form-container">
      <div className="contact">
        <h4>Contact A Consultant Today!</h4>
        <p>Edmonton, AB, Canada</p>
        <p>business@icpp.ca</p>
      </div>
      <form onSubmit={handleSubmit} className="my-5">
        {/* Bootstrap classes used for styling */}
        <div className="row">
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Phone"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
            />
          </div>
          <div className="col-md-6 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Company Name"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-md-12 mb-3">
            <textarea
              type="text"
              className="form-control"
              placeholder="Message"
              name="message"
              value={formData.message}
              onChange={handleChange}
              rows={4}
            />
          </div>
        </div>
        <div className="row">
          <div className="col-md-12 mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Location"
              name="location"
              value={formData.location}
              onChange={handleChange}
            />
          </div>
        </div>
        {/* Add other form fields similarly */}
        <div className="row">
          <div className="col-md-12">
            <button type="submit" className="btn btn-primary">Submit</button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ContactForm;
