import React, { useState } from 'react';
import { Form, Row, Col, Button } from 'react-bootstrap';
import axios from 'axios';
import '../styles/contact.css'; // Import your custom styles here

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: '',
    companyName: '',
    location: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/submit-form', formData);
      alert('Form submitted successfully!');
      // Additional logic after successful form submission if needed
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('There was an error submitting the form to the application');
    }
  };

  return (
    <div className="contact-section" style={{ background: '#000', color: '#fff', padding: '40px', textAlign: 'center' }}>
      <h2 style={{ color: '#ff0000', marginBottom: '30px', fontFamily: 'Your-Font' }}>
        Get in Touch
      </h2>
      <Row className="justify-content-center">
        <Col md={6}>
          <Form onSubmit={handleSubmit}>
            {/* Form fields... */}
            {/* (Your existing form code remains the same) */}
          </Form>
        </Col>
        <Col md={6} className="contact-info">
          <div>
            <h4>Contact Us</h4>
            <p>Edmonton, AB, Canada</p>
            <p>business@ipcc.ca</p>
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default ContactForm;
