import React, { useEffect, useRef } from 'react';
import { Card, Button } from 'react-bootstrap';
import { gsap } from 'gsap';

const ServiceCard = ({ title, description, imageUrl }) => {
  const cardRef = useRef(null);

  useEffect(() => {
    gsap.from(cardRef.current, {
      opacity: 0,
      y: 50,
      duration: 0.8,
      ease: 'power3.out',
    });
  }, []);

  return (
    <Card ref={cardRef} className="service-card border-0 shadow-sm mb-4">
      <Card.Img variant="top" src={imageUrl} className="card-img-top rounded-top" />
      <Card.Body className="bg-white rounded-bottom">
        <Card.Title className="text-primary mb-3">{title}</Card.Title>
        <Card.Text className="text-muted">{description}</Card.Text>
        <Button variant="primary" className="btn-sm">Contact</Button>
      </Card.Body>
    </Card>
  );
};

export default ServiceCard;
